  case 13
    pts=[1334 1386 1435 1719 1755 1818 1345 1350 1410 1417 1749 1802 ...
         1810;1738 1692 1649 1388 1355 1295 1729 1724 1671 1665 1361 1311 1303];
  case 14
    c=[1700;1600];
    r=200;
    theta=(0:15:359)*pi/180;
    pts=repmat(c,1,length(theta))+r*[cos(theta);sin(theta)];
  case 21
    pts=[1730 1497 1384 1515 1729 1725 1718 1714 1710 1704 1699 1693 1686 1680 1671 1662 1655 1649 1637 1628 1619 1571 1562 1548 1541 1531 1511 1502 1464 1457 1452 1443 1438 1433 1426 1416 1411 1407 1403 1399 1396 1390 1389 1385 1384 1383 1382 1383 1384 1384 1385 1387 1389 1391 1394 1398 1402 1407 1412 1417 1424 1430 1437 1445 1452 1461 1470 1483 1492;1683 1920 1715 1555 1680 1670 1653 1645 1638 1629 1623 1615 1607 1600 1592 1585 1579 1575 1568 1563 1559 1548 1549 1549 1550 1551 1557 1560 1578 1583 1586 1592 1597 1603 1611 1624 1635 1641 1647 1654 1665 1679 1681 1691 1711 1731 1741 1750 1760 1766 1769 1789 1797 1803 1811 1820 1830 1840 1848 1855 1865 1873 1880 1887 1893 1900 1906 1913 1918];
  case 22
